select FirstName,lastName,age from (select FirstName,lastName,floor(((datediff(curdate(),birthdate))/365.242))as age from employees)as age_name_surname_table group by FirstName,LastName,age having age in(select age from (select floor(((datediff(curdate(),birthdate))/365.242))as age from employees order by age desc limit 3)as age_table);